[asy]
for(int i=0;i<5;++i){
for(int j=0;j<5;++j){
filldraw(circle((i,j),0.4),white);
if(i<4 && j<4){
draw((i+0.4,j+0.4)--(i+1-0.4,j+0.4)--(i+1-0.4,j+1-0.4)--(i+0.4,j+1-0.4)--cycle);
}
}
}
[/asy]
